import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile } from "../types";
import "./MyPage.css";

const MyPage: React.FC = () => {
  const { user, isAuthenticated } = useAuth();
  const { userId } = useParams<{ userId: string }>();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isCardFlipped, setIsCardFlipped] = useState(false);

  useEffect(() => {
    // „É¶„Éº„Ç∂„ÉºID„ÅåÊåáÂÆö„Åï„Çå„Å¶„ÅÑ„ÇãÂ†¥Âêà„ÅØ„Åù„ÅÆ„É¶„Éº„Ç∂„Éº„ÅÆ„Éó„É≠„Éï„Ç£„Éº„É´„ÇíË°®Á§∫
    // ÊåáÂÆö„Åï„Çå„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÅØ„É≠„Ç∞„Ç§„É≥„É¶„Éº„Ç∂„Éº„ÅÆ„Éó„É≠„Éï„Ç£„Éº„É´„ÇíË°®Á§∫
    const targetUserId = userId || user?.id || '1';

    // „É¢„ÉÉ„ÇØ„Éó„É≠„Éï„Ç£„Éº„É´„Éá„Éº„Çø
    const mockProfile: Profile = {
      id: "1",
      userId: targetUserId,
      profileImageUrl:
        "https://via.placeholder.com/300x180/667eea/ffffff?text=Meetolio+ÂêçÂà∫",
      jobTitle: "„Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„Ç®„É≥„Ç∏„Éã„Ç¢",
      company: "Ê†™Âºè‰ºöÁ§æ„ÉÜ„ÉÉ„ÇØ„ÇΩ„É™„É•„Éº„Ç∑„Éß„É≥",
      bio: "React„ÄÅTypeScript„ÄÅNode.js„Çí‰ΩøÁî®„Åó„ÅüWeb„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥ÈñãÁô∫„Å´Âæì‰∫ã„Åó„Å¶„ÅÑ„Åæ„Åô„ÄÇ„É¶„Éº„Ç∂„Éº‰ΩìÈ®ì„ÇíÈáçË¶ñ„Åó„ÅüUI/UX„Éá„Ç∂„Ç§„É≥„ÅåÂæóÊÑè„Åß„Åô„ÄÇÊñ∞„Åó„ÅÑÊäÄË°ì„ÅÆÁøíÂæó„Å®ÂÆüË∑µÁöÑ„Å™„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥ÈñãÁô∫„Å´ÊÉÖÁÜ±„ÇíÊåÅ„Å£„Å¶Âèñ„ÇäÁµÑ„Çì„Åß„ÅÑ„Åæ„Åô„ÄÇ",
      contactInfo: {
        email: "test@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "@techdev",
          linkedin: "linkedin.com/in/techdev",
          facebook: "facebook.com/techdev",
          instagram: "@techdev",
          github: "github.com/techdev",
        },
        website: "https://techdev.example.com",
      },
      skills: ["React", "TypeScript", "Node.js", "CSS", "Git"],
      interests: ["WebÈñãÁô∫", "UI/UX", "ÊäÄË°ìÊõ∏", "„Ç´„É≥„Éï„Ç°„É¨„É≥„Çπ"],
      isPublic: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
  }, [userId, user]);

  if (!profile) {
    return (
      <div className="mypage">
        <div className="container">
          <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>
        </div>
      </div>
    );
  }

  const handleCardFlip = () => {
    setIsCardFlipped(!isCardFlipped);
  };

  // Ë°®Á§∫„Åô„Çã„É¶„Éº„Ç∂„ÉºÂêç„ÇíÊ±∫ÂÆö
  const displayName = user?.name || "„ÉÜ„ÉÉ„ÇØ Â§™ÈÉé";
  const isOwnProfile = isAuthenticated && user?.id === profile.userId;

  return (
    <div className="mypage">
      <div className="container">
        <div className="mypage-header">
          <h1>„Éó„É≠„Éï„Ç£„Éº„É´</h1>
          {isOwnProfile && (
            <div className="mypage-actions">
              <Link to="/profile/edit" className="btn btn-primary">
                „Éó„É≠„Éï„Ç£„Éº„É´Á∑®ÈõÜ
              </Link>
              <Link to="/settings" className="btn btn-secondary">
                Ë®≠ÂÆö
              </Link>
            </div>
          )}
        </div>

        <div className="profile-section">
          {/* ÂêçÂà∫ÁîªÂÉè„Çª„ÇØ„Ç∑„Éß„É≥ */}
          <div className="business-card-section">
            <div className="card-container">
              <div
                className={`business-card ${isCardFlipped ? "flipped" : ""}`}
                onClick={handleCardFlip}
              >
                {/* ÂêçÂà∫„ÅÆË°®Èù¢ */}
                <div className="card-front">
                  <div className="card-header">
                    <div className="company-info">
                      <h2 className="company-name">{profile.company}</h2>
                      <p className="company-details">Web Development Company</p>
                    </div>
                    <div className="card-status">
                      <span
                        className={`status-badge ${
                          profile.isPublic ? "public" : "private"
                        }`}
                      >
                        {profile.isPublic ? "ÂÖ¨Èñã" : "ÈùûÂÖ¨Èñã"}
                      </span>
                    </div>
                  </div>

                  <div className="card-body">
                    <div className="contact-info-left">
                      <div className="contact-item">
                        <span className="contact-label">„Äí</span>
                        <span className="contact-value">100-0001</span>
                      </div>
                      <div className="contact-item">
                        <span className="contact-label">‰ΩèÊâÄ</span>
                        <span className="contact-value">
                          Êù±‰∫¨ÈÉΩÂçÉ‰ª£Áî∞Âå∫ÂçÉ‰ª£Áî∞1-1
                        </span>
                      </div>
                      <div className="contact-item">
                        <span className="contact-label">TEL</span>
                        <span className="contact-value">03-1234-5678</span>
                      </div>
                      <div className="contact-item">
                        <span className="contact-label">FAX</span>
                        <span className="contact-value">03-1234-5679</span>
                      </div>
                      <div className="contact-item">
                        <span className="contact-label">Mobile</span>
                        <span className="contact-value">
                          {profile.contactInfo.phone}
                        </span>
                      </div>
                      <div className="contact-item">
