import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile } from "../types";
import { Stage, Layer, Rect, Text, Transformer } from "react-konva";
import Konva from "konva";
import "./BusinessCardDesign.css";

interface TextElement {
  id: string;
  text: string;
  x: number;
  y: number;
  fontSize: number;
  fontFamily: string;
  fill: string;
  width: number;
  align: string;
  draggable: boolean;
}

const BusinessCardDesign: React.FC = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [currentSide, setCurrentSide] = useState<'front' | 'back'>('front');

  const [textElements, setTextElements] = useState<TextElement[]>([]);
  const [selectedId, setSelectedId] = useState<string | null>(null);
  const [editingText, setEditingText] = useState<string>("");
  const [fontSize, setFontSize] = useState(16);
  const [textColor, setTextColor] = useState("#000000");
  const [fontFamily, setFontFamily] = useState("Arial");

  const transformerRef = useRef<Konva.Transformer>(null);
  const stageRef = useRef<Konva.Stage>(null);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }

    // モックプロフィールデータを取得
    const mockProfile: Profile = {
      id: "1",
      userId: user?.id || "1",
      company: "テック株式会社",
      jobTitle: "フロントエンドエンジニア",
      bio: "Web開発に携わって5年目です。React、TypeScript、Node.jsを中心に開発を行っています。ユーザビリティを重視した設計を心がけています。",
      skills: [
        "React",
        "TypeScript",
        "Node.js",
        "CSS",
        "HTML",
        "JavaScript",
        "Git",
        "AWS",
      ],
      interests: ["プログラミング", "読書", "旅行", "料理", "音楽"],
      contactInfo: {
        email: "taro.tech@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "taro_tech",
          linkedin: "taro-tech",
          github: "taro-tech",
          instagram: "taro_tech_dev",
        },
        website: "https://taro-tech.dev",
      },
      isPublic: true,
      createdAt: new Date("2024-01-01"),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
    setIsLoading(false);
  }, [isAuthenticated, user, navigate]);

  // 初期テキスト要素を設定
  useEffect(() => {
    if (profile) {
      const initialTexts: TextElement[] = currentSide === 'front' ? [
        {
          id: 'company',
          text: profile.company || "会社名",
          x: 200,
          y: 60,
          fontSize: 18,
          fontFamily: "Arial",
          fill: "#000000",
          width: 400,
          align: "center",
          draggable: true,
        },
        {
          id: 'jobTitle',
          text: profile.jobTitle || "役職",
          x: 200,
          y: 90,
          fontSize: 14,
          fontFamily: "Arial",
          fill: "#666666",
          width: 400,
          align: "center",
          draggable: true,
        },
        {
          id: 'name',
          text: user?.name || "氏名",
          x: 200,
          y: 120,
          fontSize: 16,
          fontFamily: "Arial",
          fill: "#000000",
          width: 400,
          align: "center",
          draggable: true,
        }
      ] : [
        {
          id: 'email',
          text: profile.contactInfo.email || "メールアドレス",
          x: 200,
          y: 80,
          fontSize: 12,
          fontFamily: "Arial",
          fill: "#000000",
          width: 400,
          align: "center",
          draggable: true,
        },
        {
          id: 'phone',
          text: profile.contactInfo.phone || "電話番号",
          x: 200,
          y: 110,
          fontSize: 12,
          fontFamily: "Arial",
          fill: "#000000",
          width: 400,
          align: "center",
          draggable: true,
        },
        {
          id: 'website',
          text: profile.contactInfo.website || "ウェブサイト",
          x: 200,
          y: 140,
          fontSize: 12,
          fontFamily: "Arial",
          fill: "#000000",
          width: 400,
          align: "center",
          draggable: true,
        }
      ];

      setTextElements(initialTexts);
    }
  }, [profile, currentSide, user]);

  // 選択された要素のTransformerを更新
  useEffect(() => {
    if (selectedId && transformerRef.current) {
      const node = stageRef.current?.findOne(`#${selectedId}`);
      if (node) {
        transformerRef.current.nodes([node]);
        transformerRef.current.getLayer()?.batchDraw();
      }
    }
  }, [selectedId]);



  const handleTextClick = (id: string) => {
    setSelectedId(id);
    const element = textElements.find(el => el.id === id);
    if (element) {
      setEditingText(element.text);
    }
  };

  const handleTextEdit = (id: string, newText: string) => {
    setTextElements(prev => 
      prev.map(el => 
        el.id === id ? { ...el, text: newText } : el
      )
    );
    setEditingText("");
  };

  const handleTextUpdate = (id: string, updates: Partial<TextElement>) => {
    setTextElements(prev => 
      prev.map(el => 
        el.id === id ? { ...el, ...updates } : el
      )
    );
  };

  const handleAddText = () => {
    const newText: TextElement = {
      id: `text-${Date.now()}`,
      text: "新しいテキスト",
      x: 100,
      y: 100,
      fontSize: fontSize,
      fontFamily: fontFamily,
      fill: textColor,
      width: 200,
      align: "left",
      draggable: true,
    };
    setTextElements(prev => [...prev, newText]);
    setSelectedId(newText.id);
  };

  const handleDeleteText = () => {
    if (selectedId) {
      setTextElements(prev => prev.filter(el => el.id !== selectedId));
      setSelectedId(null);
    }
  };

  const handleSave = async () => {
    try {
      await new Promise((resolve) => setTimeout(resolve, 1000));
      console.log("名刺デザインを保存しました");
      console.log("表面デザイン:", frontDesign);
      console.log("裏面デザイン:", backDesign);
      navigate("/business-card/edit");
    } catch (error) {
      console.error("名刺デザインの保存に失敗しました:", error);
    }
  };

  const handleCancel = () => {
    navigate("/business-card/edit");
  };

  const handleExportImage = () => {
    if (stageRef.current) {
      const dataURL = stageRef.current.toDataURL();
      const link = document.createElement('a');
      link.download = `business-card-${currentSide}.png`;
      link.href = dataURL;
      link.click();
    }
  };

  if (isLoading) {
    return (
      <div className="business-card-design">
        <div className="container">
          <div className="loading">読み込み中...</div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="business-card-design">
        <div className="container">
          <div className="error">プロフィールが見つかりません</div>
        </div>
      </div>
    );
  }

  return (
    <div className="business-card-design">
      <div className="container">
        <div className="design-header">
          <h1>名刺デザイン</h1>
          <p>名刺の表面・裏面をデザインできます</p>
        </div>

        <div className="design-container">
          {/* サイド選択タブ */}
          <div className="side-tabs">
            <button
              className={`side-tab ${currentSide === 'front' ? 'active' : ''}`}
              onClick={() => setCurrentSide('front')}
            >
              表面
            </button>
            <button
              className={`side-tab ${currentSide === 'back' ? 'active' : ''}`}
              onClick={() => setCurrentSide('back')}
            >
              裏面
            </button>
          </div>

          <div className="design-workspace">
            {/* デザインエディター */}
            <div className="editor-container">
              <div className="editor-wrapper">
                <Stage
                  ref={stageRef}
                  width={400}
                  height={242}
                  onMouseDown={(e) => {
                    if (e.target === e.target.getStage()) {
                      setSelectedId(null);
                    }
                  }}
                >
                  <Layer>
                    {/* 背景 */}
                    <Rect
                      x={0}
                      y={0}
                      width={400}
                      height={242}
                      fill="#ffffff"
                      stroke="#e5e7eb"
                      strokeWidth={1}
                    />

                    {/* テキスト要素 */}
                    {textElements.map((element) => (
                      <Text
                        key={element.id}
                        id={element.id}
                        text={element.text}
                        x={element.x}
                        y={element.y}
                        fontSize={element.fontSize}
                        fontFamily={element.fontFamily}
                        fill={element.fill}
                        align={element.align as any}
                        width={element.width}
                        draggable={element.draggable}
                        onClick={() => handleTextClick(element.id)}
                        onTap={() => handleTextClick(element.id)}
                        onDragEnd={(e) => {
                          handleTextUpdate(element.id, {
                            x: e.target.x(),
                            y: e.target.y()
                          });
                        }}
                      />
                    ))}

                    {/* Transformer */}
                    <Transformer ref={transformerRef} />
                  </Layer>
                </Stage>
              </div>
            </div>

            {/* デザインツール */}
            <div className="design-tools">
              <div className="tool-section">
                <h3>テキスト編集</h3>
                {selectedId && (
                  <div className="text-editor">
                    <input
                      type="text"
                      value={editingText}
                      onChange={(e) => setEditingText(e.target.value)}
                      onBlur={() => handleTextEdit(selectedId, editingText)}
                      onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                          handleTextEdit(selectedId, editingText);
                        }
                      }}
                      placeholder="テキストを入力"
                      className="text-input"
                    />
                  </div>
                )}
              </div>

              <div className="tool-section">
                <h3>フォント設定</h3>
                <div className="font-controls">
                  <label>
                    フォントサイズ:
                    <input
                      type="range"
                      min="8"
                      max="48"
                      value={fontSize}
                      onChange={(e) => {
                        setFontSize(Number(e.target.value));
                        if (selectedId) {
                          handleTextUpdate(selectedId, { fontSize: Number(e.target.value) });
                        }
                      }}
                      className="font-size-slider"
                    />
                    <span>{fontSize}px</span>
                  </label>

                  <label>
                    フォント:
                    <select
                      value={fontFamily}
                      onChange={(e) => {
                        setFontFamily(e.target.value);
                        if (selectedId) {
                          handleTextUpdate(selectedId, { fontFamily: e.target.value });
                        }
                      }}
                      className="font-family-select"
                    >
                      <option value="Arial">Arial</option>
                      <option value="Helvetica">Helvetica</option>
                      <option value="Times New Roman">Times New Roman</option>
                      <option value="Georgia">Georgia</option>
                      <option value="Verdana">Verdana</option>
                    </select>
                  </label>

                  <label>
                    色:
                    <input
                      type="color"
                      value={textColor}
                      onChange={(e) => {
                        setTextColor(e.target.value);
                        if (selectedId) {
                          handleTextUpdate(selectedId, { fill: e.target.value });
                        }
                      }}
                      className="color-picker"
                    />
                  </label>
                </div>
              </div>

              <div className="tool-section">
                <h3>アクション</h3>
                <div className="action-buttons">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={handleAddText}
                  >
                    テキスト追加
                  </button>
                  {selectedId && (
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={handleDeleteText}
                    >
                      削除
                    </button>
                  )}
                  <button
                    type="button"
                    className="btn btn-success"
                    onClick={handleExportImage}
                  >
                    画像として保存
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* アクションボタン */}
          <div className="design-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleCancel}
            >
              キャンセル
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleSave}
            >
              デザインを保存
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BusinessCardDesign;
