import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile } from "../types";
import { Stage, Layer, Rect, Text } from "react-konva";
import "./BusinessCardDesign.css";

const BusinessCardDesign: React.FC = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [currentSide, setCurrentSide] = useState<'front' | 'back'>('front');
  const [frontDesign, setFrontDesign] = useState<any>(null);
  const [backDesign, setBackDesign] = useState<any>(null);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }

    // モックプロフィールデータを取得
    const mockProfile: Profile = {
      id: "1",
      userId: user?.id || "1",
      company: "テック株式会社",
      jobTitle: "フロントエンドエンジニア",
      bio: "Web開発に携わって5年目です。React、TypeScript、Node.jsを中心に開発を行っています。ユーザビリティを重視した設計を心がけています。",
      skills: [
        "React",
        "TypeScript",
        "Node.js",
        "CSS",
        "HTML",
        "JavaScript",
        "Git",
        "AWS",
      ],
      interests: ["プログラミング", "読書", "旅行", "料理", "音楽"],
      contactInfo: {
        email: "taro.tech@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "taro_tech",
          linkedin: "taro-tech",
          github: "taro-tech",
          instagram: "taro_tech_dev",
        },
        website: "https://taro-tech.dev",
      },
      isPublic: true,
      createdAt: new Date("2024-01-01"),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
    setIsLoading(false);
  }, [isAuthenticated, user, navigate]);

  const handleSave = async () => {
    try {
      // デザインを保存する処理（モック）
      await new Promise((resolve) => setTimeout(resolve, 1000));
      console.log("名刺デザインを保存しました");
      console.log("表面デザイン:", frontDesign);
      console.log("裏面デザイン:", backDesign);
      navigate("/business-card/edit");
    } catch (error) {
      console.error("名刺デザインの保存に失敗しました:", error);
    }
  };

  const handleCancel = () => {
    navigate("/business-card/edit");
  };

  const handleDesignChange = () => {
    // 現在のデザイン状態を保存
    const currentDesign = {
      side: currentSide,
      timestamp: new Date().toISOString(),
      profile: profile
    };

    if (currentSide === 'front') {
      setFrontDesign(currentDesign);
    } else {
      setBackDesign(currentDesign);
    }
  };

  // サイドが変更されたときにデザインを更新
  useEffect(() => {
    handleDesignChange();
  }, [currentSide, profile, user]);

  if (isLoading) {
    return (
      <div className="business-card-design">
        <div className="container">
          <div className="loading">読み込み中...</div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="business-card-design">
        <div className="container">
          <div className="error">プロフィールが見つかりません</div>
        </div>
      </div>
      );
  }

  return (
    <div className="business-card-design">
      <div className="container">
        <div className="design-header">
          <h1>名刺デザイン</h1>
          <p>名刺の表面・裏面をデザインできます</p>
        </div>

        <div className="design-container">
          {/* サイド選択タブ */}
          <div className="side-tabs">
            <button
              className={`side-tab ${currentSide === 'front' ? 'active' : ''}`}
              onClick={() => setCurrentSide('front')}
            >
              表面
            </button>
            <button
              className={`side-tab ${currentSide === 'back' ? 'active' : ''}`}
              onClick={() => setCurrentSide('back')}
            >
              裏面
            </button>
          </div>

          {/* デザインエディター */}
          <div className="editor-container">
            <div className="editor-wrapper">
              <Stage width={400} height={242}>
                <Layer>
                  {/* 背景 */}
                  <Rect
                    x={0}
                    y={0}
                    width={400}
                    height={242}
                    fill="#ffffff"
                    stroke="#e5e7eb"
                    strokeWidth={1}
                  />

                  {/* 表面のテキスト */}
                  {currentSide === 'front' ? (
                    <>
                      <Text
                        text={profile.company || "会社名"}
                        x={200}
                        y={60}
                        fontSize={18}
                        fontFamily="Arial"
                        fill="#000000"
                        align="center"
                        width={400}
                      />
                      <Text
                        text={profile.jobTitle || "役職"}
                        x={200}
                        y={90}
                        fontSize={14}
                        fontFamily="Arial"
                        fill="#666666"
                        align="center"
                        width={400}
                      />
                      <Text
                        text={user?.name || "氏名"}
                        x={200}
                        y={120}
                        fontSize={16}
                        fontFamily="Arial"
                        fill="#000000"
                        align="center"
                        width={400}
                      />
                    </>
                  ) : (
                    <>
                      {/* 裏面のテキスト */}
                      <Text
                        text={profile.contactInfo.email || "メールアドレス"}
                        x={200}
                        y={80}
                        fontSize={12}
                        fontFamily="Arial"
                        fill="#000000"
                        align="center"
                        width={400}
                      />
                      <Text
                        text={profile.contactInfo.phone || "電話番号"}
                        x={200}
                        y={110}
                        fontSize={12}
                        fontFamily="Arial"
                        fill="#000000"
                        align="center"
                        width={400}
                      />
                      <Text
                        text={profile.contactInfo.website || "ウェブサイト"}
                        x={200}
                        y={140}
                        fontSize={12}
                        fontFamily="Arial"
                        fill="#000000"
                        align="center"
                        width={400}
                      />
                    </>
                  )}
                </Layer>
              </Stage>
            </div>
          </div>

          {/* アクションボタン */}
          <div className="design-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleCancel}
            >
              キャンセル
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleSave}
            >
              デザインを保存
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BusinessCardDesign;
