import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile, ProfileForm } from "../types";
import "./MyPage.css";

const MyPage: React.FC = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }

    // „É¢„ÉÉ„ÇØ„Éó„É≠„Éï„Ç£„Éº„É´„Éá„Éº„Çø
    const mockProfile: Profile = {
      id: "1",
      userId: user?.id || "1",
      profileImageUrl: "https://via.placeholder.com/150",
      jobTitle: "„Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„Ç®„É≥„Ç∏„Éã„Ç¢",
      company: "Ê†™Âºè‰ºöÁ§æ„ÉÜ„ÉÉ„ÇØ„ÇΩ„É™„É•„Éº„Ç∑„Éß„É≥",
      bio: "React„ÄÅTypeScript„ÄÅNode.js„Çí‰ΩøÁî®„Åó„ÅüWeb„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥ÈñãÁô∫„Å´Âæì‰∫ã„Åó„Å¶„ÅÑ„Åæ„Åô„ÄÇ„É¶„Éº„Ç∂„Éº‰ΩìÈ®ì„ÇíÈáçË¶ñ„Åó„ÅüUI/UX„Éá„Ç∂„Ç§„É≥„ÅåÂæóÊÑè„Åß„Åô„ÄÇ",
      contactInfo: {
        email: user?.email || "test@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "@techdev",
          linkedin: "linkedin.com/in/techdev",
        },
        website: "https://techdev.example.com",
      },
      skills: ["React", "TypeScript", "Node.js", "CSS", "Git"],
      interests: ["WebÈñãÁô∫", "UI/UX", "ÊäÄË°ìÊõ∏", "„Ç´„É≥„Éï„Ç°„É¨„É≥„Çπ"],
      isPublic: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
  }, [isAuthenticated, user, navigate]);

  if (!isAuthenticated || !user) {
    return null;
  }

  if (!profile) {
    return (
      <div className="mypage">
        <div className="container">
          <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="mypage">
      <div className="container">
        <div className="mypage-header">
          <h1>„Éû„Ç§„Éö„Éº„Ç∏</h1>
          <div className="mypage-actions">
            <button
              onClick={() => setIsEditing(!isEditing)}
              className="btn btn-primary"
            >
              {isEditing ? "„Ç≠„É£„É≥„Çª„É´" : "„Éó„É≠„Éï„Ç£„Éº„É´Á∑®ÈõÜ"}
            </button>
            <Link to="/settings" className="btn btn-secondary">
              Ë®≠ÂÆö
            </Link>
          </div>
        </div>

        <div className="profile-section">
          <div className="profile-header">
            <div className="profile-image">
              <img
                src={
                  profile.profileImageUrl || "https://via.placeholder.com/150"
                }
                alt="„Éó„É≠„Éï„Ç£„Éº„É´ÁîªÂÉè"
              />
              {isEditing && (
                <button className="image-upload-btn">üì∑ ÁîªÂÉèÂ§âÊõ¥</button>
              )}
            </div>

            <div className="profile-info">
              <h2 className="profile-name">{user.name}</h2>
              {profile.jobTitle && (
                <p className="profile-job">{profile.jobTitle}</p>
              )}
              {profile.company && (
                <p className="profile-company">{profile.company}</p>
              )}
              <div className="profile-status">
                <span
                  className={`status-badge ${
                    profile.isPublic ? "public" : "private"
                  }`}
                >
                  {profile.isPublic ? "ÂÖ¨Èñã" : "ÈùûÂÖ¨Èñã"}
                </span>
              </div>
            </div>
          </div>

          {profile.bio && (
            <div className="profile-bio">
              <h3>Ëá™Â∑±Á¥π‰ªã</h3>
              <p>{profile.bio}</p>
            </div>
          )}

          <div className="profile-details">
            <div className="contact-info">
              <h3>ÈÄ£Áµ°ÂÖàÊÉÖÂ†±</h3>
              <div className="contact-grid">
                <div className="contact-item">
                  <span className="contact-label">„É°„Éº„É´„Ç¢„Éâ„É¨„Çπ</span>
                  <span className="contact-value">
                    {profile.contactInfo.email}
                  </span>
                </div>
                {profile.contactInfo.phone && (
                  <div className="contact-item">
                    <span className="contact-label">ÈõªË©±Áï™Âè∑</span>
                    <span className="contact-value">
                      {profile.contactInfo.phone}
                    </span>
                  </div>
                )}
                {profile.contactInfo.website && (
                  <div className="contact-item">
                    <span className="contact-label">„Ç¶„Çß„Éñ„Çµ„Ç§„Éà</span>
                    <a
                      href={profile.contactInfo.website}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="contact-link"
                    >
                      {profile.contactInfo.website}
                    </a>
                  </div>
                )}
              </div>

              {profile.contactInfo.sns && (
                <div className="sns-links">
                  <h4>SNS</h4>
                  <div className="sns-grid">
                    {profile.contactInfo.sns.twitter && (
                      <a
                        href={`https://twitter.com/${profile.contactInfo.sns.twitter}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="sns-link twitter"
                      >
                        üê¶ {profile.contactInfo.sns.twitter}
                      </a>
                    )}
                    {profile.contactInfo.sns.linkedin && (
                      <a
                        href={`https://${profile.contactInfo.sns.linkedin}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="sns-link linkedin"
                      >
                        üíº {profile.contactInfo.sns.linkedin}
                      </a>
                    )}
                  </div>
                </div>
              )}
            </div>

            <div className="skills-interests">
              <div className="skills-section">
                <h3>„Çπ„Ç≠„É´„ÉªÂ∞ÇÈñÄÂàÜÈáé</h3>
                <div className="tags">
                  {profile.skills.map((skill, index) => (
                    <span key={index} className="tag skill-tag">
                      {skill}
                    </span>
                  ))}
                </div>
