import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile } from "../types";

import "./BusinessCardEdit.css";

const BusinessCardEdit: React.FC = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [uploadedImages, setUploadedImages] = useState<{
    front?: string;
    back?: string;
  }>({});

  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }

    // „É¢„ÉÉ„ÇØ„Éó„É≠„Éï„Ç£„Éº„É´„Éá„Éº„Çø„ÇíÂèñÂæó
    const mockProfile: Profile = {
      id: "1",
      userId: user?.id || "1",
      company: "„ÉÜ„ÉÉ„ÇØÊ†™Âºè‰ºöÁ§æ",
      jobTitle: "„Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„Ç®„É≥„Ç∏„Éã„Ç¢",
      bio: "WebÈñãÁô∫„Å´Êê∫„Çè„Å£„Å¶5Âπ¥ÁõÆ„Åß„Åô„ÄÇReact„ÄÅTypeScript„ÄÅNode.js„Çí‰∏≠ÂøÉ„Å´ÈñãÁô∫„ÇíË°å„Å£„Å¶„ÅÑ„Åæ„Åô„ÄÇ„É¶„Éº„Ç∂„Éì„É™„ÉÜ„Ç£„ÇíÈáçË¶ñ„Åó„ÅüË®≠Ë®à„ÇíÂøÉ„Åå„Åë„Å¶„ÅÑ„Åæ„Åô„ÄÇ",
      skills: [
        "React",
        "TypeScript",
        "Node.js",
        "CSS",
        "HTML",
        "JavaScript",
        "Git",
        "AWS",
      ],
      interests: ["„Éó„É≠„Ç∞„É©„Éü„É≥„Ç∞", "Ë™≠Êõ∏", "ÊóÖË°å", "ÊñôÁêÜ", "Èü≥Ê•Ω"],
      contactInfo: {
        email: "taro.tech@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "taro_tech",
          linkedin: "taro-tech",
          github: "taro-tech",
          instagram: "taro_tech_dev",
        },
        website: "https://taro-tech.dev",
      },
      isPublic: true,
      createdAt: new Date("2024-01-01"),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
    setIsLoading(false);
  }, [isAuthenticated, user, navigate]);

  const handleImageUpload = (side: "front" | "back", file: File) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const result = e.target?.result as string;
      setUploadedImages((prev) => ({
        ...prev,
        [side]: result,
      }));
      console.log(`${side}Èù¢„ÅÆÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Åæ„Åó„Åü:`, file.name);
    };
    reader.readAsDataURL(file);
  };

  const handleSave = async () => {
    if (!profile) return;

    try {
      // „É¢„ÉÉ„ÇØAPIÂëº„Å≥Âá∫„Åó
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // ÊàêÂäüÊôÇ„ÅÆÂá¶ÁêÜ
      console.log("ÂêçÂà∫„Çí‰øùÂ≠ò„Åó„Åæ„Åó„Åü:", profile);
      console.log("„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åï„Çå„ÅüÁîªÂÉè:", uploadedImages);
      navigate("/profile/edit");
    } catch (error) {
      console.error("ÂêçÂà∫„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:", error);
    }
  };

  const handleCancel = () => {
    navigate("/profile/edit");
  };

  if (isLoading) {
    return (
      <div className="business-card-edit">
        <div className="container">
          <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="business-card-edit">
        <div className="container">
          <div className="error">„Éó„É≠„Éï„Ç£„Éº„É´„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì</div>
        </div>
      </div>
    );
  }

  return (
    <div className="business-card-edit">
      <div className="container">
        <div className="edit-form">
          {/* ÂêçÂà∫„Éó„É¨„Éì„É•„ÉºÔºàË°®Èù¢„ÉªË£èÈù¢Ôºâ */}
          <div className="form-group">
            <label>ÁèæÂú®„ÅÆÂêçÂà∫</label>
            <div className="card-preview-container">
              <div className="preview-section">
                <h4 className="preview-title">Ë°®Èù¢</h4>
                <div className="card-preview-item">
                  <img
                    src={uploadedImages.front || "/img/sample.jpeg"}
                    alt="ÂêçÂà∫ÔºàË°®Èù¢Ôºâ"
                    className="card-preview-image"
                  />
                </div>
                <div className="preview-actions">
                  <div className="action-item">
                    <input
                      type="file"
                      id="front-upload"
                      accept="image/*"
                      className="file-input"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (file) {
                          handleImageUpload("front", file);
                        }
                      }}
                    />
                    <label
                      htmlFor="front-upload"
                      className="action-btn upload-btn"
                    >
                      üìÅ
                    </label>
                    <span className="action-label">ÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ</span>
                  </div>
                  <div className="action-item">
                    <button type="button" className="action-btn design-btn">
                      ‚úè
                    </button>
                    <span className="action-label">Ëá™ÂàÜ„Åß„Éá„Ç∂„Ç§„É≥</span>
                  </div>
                </div>
              </div>

              <div className="preview-section">
                <h4 className="preview-title">Ë£èÈù¢</h4>
                <div className="card-preview-item">
                  <img
                    src={uploadedImages.back || "/img/sample2.jpeg"}
                    alt="ÂêçÂà∫ÔºàË£èÈù¢Ôºâ"
                    className="card-preview-image"
                  />
                </div>
                <div className="preview-actions">
                  <div className="action-item">
                    <input
                      type="file"
                      id="back-upload"
                      accept="image/*"
                      className="file-input"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (file) {
                          handleImageUpload("back", file);
                        }
                      }}
                    />
                    <label
                      htmlFor="back-upload"
                      className="action-btn upload-btn"
                    >
                      üìÅ
                    </label>
                    <span className="action-label">ÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ</span>
                  </div>
                  <div className="action-item">
                    <button type="button" className="action-btn design-btn">
                      ‚úè
                    </button>
                    <span className="action-label">Ëá™ÂàÜ„Åß„Éá„Ç∂„Ç§„É≥</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* „Ç¢„ÇØ„Ç∑„Éß„É≥„Éú„Çø„É≥ */}
          <div className="form-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleCancel}
            >
              „Ç≠„É£„É≥„Çª„É´
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleSave}
            >
              ‰øùÂ≠ò„Åô„Çã
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BusinessCardEdit;
