import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { ChangeEmailForm, ChangePasswordForm, DeleteAccountForm } from "../types";
import "./Settings.css";

const Settings: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("profile");

  // メールアドレス変更
  const [showEmailChange, setShowEmailChange] = useState(false);
  const [emailForm, setEmailForm] = useState<ChangeEmailForm>({
    currentEmail: user?.email || "",
    newEmail: "",
    password: "",
  });
  const [isChangingEmail, setIsChangingEmail] = useState(false);

  // パスワード変更
  const [showPasswordChange, setShowPasswordChange] = useState(false);
  const [passwordForm, setPasswordForm] = useState<ChangePasswordForm>({
    currentPassword: "",
    newPassword: "",
    confirmNewPassword: "",
  });
  const [isChangingPassword, setIsChangingPassword] = useState(false);

  // 退会処理
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [deleteForm, setDeleteForm] = useState<DeleteAccountForm>({
    reason: "",
    password: "",
  });
  const [isDeletingAccount, setIsDeletingAccount] = useState(false);

  const handleLogout = () => {
    if (window.confirm("ログアウトしますか？")) {
      logout();
      navigate("/login");
    }
  };

  // メールアドレス変更
  const handleEmailChange = async () => {
    if (!emailForm.newEmail || !emailForm.password) {
      alert("新しいメールアドレスとパスワードを入力してください");
      return;
    }

    if (emailForm.newEmail === emailForm.currentEmail) {
      alert("新しいメールアドレスが現在のものと同じです");
      return;
    }

    setIsChangingEmail(true);
    try {
      // モックAPI呼び出し
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // 成功時の処理
      alert("メールアドレスが変更されました");
      setShowEmailChange(false);
      setEmailForm({
        currentEmail: emailForm.newEmail,
        newEmail: "",
        password: "",
      });
    } catch (error) {
      alert("メールアドレスの変更に失敗しました");
    } finally {
      setIsChangingEmail(false);
    }
  };

  // パスワード変更
  const handlePasswordChange = async () => {
    if (!passwordForm.currentPassword || !passwordForm.newPassword || !passwordForm.confirmNewPassword) {
      alert("すべてのフィールドを入力してください");
      return;
    }

    if (passwordForm.newPassword !== passwordForm.confirmNewPassword) {
      alert("新しいパスワードが一致しません");
      return;
    }

    if (passwordForm.newPassword.length < 6) {
      alert("新しいパスワードは6文字以上で入力してください");
      return;
    }

    setIsChangingPassword(true);
    try {
      // モックAPI呼び出し
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // 成功時の処理
      alert("パスワードが変更されました");
      setShowPasswordChange(false);
      setPasswordForm({
        currentPassword: "",
        newPassword: "",
        confirmNewPassword: "",
      });
    } catch (error) {
      alert("パスワードの変更に失敗しました");
    } finally {
      setIsChangingPassword(false);
    }
  };

  // 退会処理
  const handleDeleteAccount = async () => {
    if (!deleteForm.reason || !deleteForm.password) {
      alert("退会理由とパスワードを入力してください");
      return;
    }

    if (
      window.confirm(
        "本当にアカウントを削除しますか？この操作は取り消せません。"
      )
    ) {
      setIsDeletingAccount(true);
      try {
        // モックAPI呼び出し
        await new Promise((resolve) => setTimeout(resolve, 1000));

        // 成功時の処理
        alert("アカウントが削除されました");
        logout();
        navigate("/login");
      } catch (error) {
        alert("アカウントの削除に失敗しました");
      } finally {
        setIsDeletingAccount(false);
      }
    }
  };

  if (!user) {
    return null;
  }

  return (
    <div className="settings">
      <div className="container">
        <div className="settings-header">
          <h1>設定</h1>
        </div>

        <div className="settings-content">
          <div className="settings-sidebar">
            <nav className="settings-nav">
              <button
                className={`nav-item ${
                  activeTab === "profile" ? "active" : ""
                }`}
                onClick={() => setActiveTab("profile")}
              >
                プロフィール設定
              </button>
              <button
                className={`nav-item ${
                  activeTab === "account" ? "active" : ""
                }`}
                onClick={() => setActiveTab("account")}
              >
                アカウント設定
              </button>
              <button
                className={`nav-item ${
                  activeTab === "privacy" ? "active" : ""
                }`}
                onClick={() => setActiveTab("privacy")}
              >
                プライバシー設定
              </button>
              <button
                className={`nav-item ${
                  activeTab === "notifications" ? "active" : ""
                }`}
                onClick={() => setActiveTab("notifications")}
              >
                通知設定
              </button>
              <button
                className={`nav-item ${activeTab === "danger" ? "active" : ""}`}
                onClick={() => setActiveTab("danger")}
              >
                危険な操作
              </button>
            </nav>
          </div>

          <div className="settings-main">
            {activeTab === "profile" && (
              <div className="settings-section">
                <h2>プロフィール設定</h2>
                <p>プロフィール情報の編集は、マイページから行えます。</p>
                <button
                  onClick={() => navigate("/mypage")}
                  className="btn btn-primary"
                >
                  マイページへ移動
                </button>
              </div>
            )}

            {activeTab === "account" && (
              <div className="settings-section">
                <h2>アカウント設定</h2>

                {/* メールアドレス変更 */}
                <div className="form-group">
                  <label className="form-label">メールアドレス</label>
                  <div className="current-email">
                    <span className="email-display">{user.email}</span>
                    {!showEmailChange && (
                      <button
                        onClick={() => setShowEmailChange(true)}
                        className="btn btn-secondary btn-small"
                      >
                        変更
                      </button>
                    )}
                  </div>

                  {showEmailChange && (
                    <div className="change-email-form">
                      <div className="form-group">
                        <label className="form-label">新しいメールアドレス</label>
                        <input
                          type="email"
                          className="form-input"
                          value={emailForm.newEmail}
                          onChange={(e) => setEmailForm({...emailForm, newEmail: e.target.value})}
                          placeholder="新しいメールアドレスを入力"
                        />
                      </div>
                      <div className="form-group">
                        <label className="form-label">現在のパスワード</label>
                        <input
                          type="password"
                          className="form-input"
                          value={emailForm.password}
                          onChange={(e) => setEmailForm({...emailForm, password: e.target.value})}
                          placeholder="現在のパスワードを入力"
                        />
                      </div>
                      <div className="form-actions">
                        <button
                          onClick={() => {
                            setShowEmailChange(false);
                            setEmailForm({
                              currentEmail: user?.email || "",
                              newEmail: "",
                              password: "",
                            });
                          }}
                          className="btn btn-secondary"
                        >
                          キャンセル
                        </button>
                        <button
                          onClick={handleEmailChange}
                          className="btn btn-primary"
                          disabled={isChangingEmail}
                        >
                          {isChangingEmail ? "変更中..." : "メールアドレスを変更"}
                        </button>
                      </div>
                    </div>
                  )}
                </div>

                {/* パスワード変更 */}
                <div className="form-group">
                  <label className="form-label">パスワード</label>
                  {!showPasswordChange && (
                    <button
                      onClick={() => setShowPasswordChange(true)}
                      className="btn btn-secondary btn-small"
                    >
                      変更
                    </button>
                  )}

                  {showPasswordChange && (
                    <div className="change-password-form">
                      <div className="form-group">
                        <label className="form-label">現在のパスワード</label>
                        <input
                          type="password"
                          className="form-input"
                          value={passwordForm.currentPassword}
                          onChange={(e) => setPasswordForm({...passwordForm, currentPassword: e.target.value})}
                          placeholder="現在のパスワードを入力"
                        />
                      </div>
                      <div className="form-group">
                        <label className="form-label">新しいパスワード</label>
                        <input
                          type="password"
                          className="form-input"
                          value={passwordForm.newPassword}
                          onChange={(e) => setPasswordForm({...passwordForm, newPassword: e.target.value})}
                          placeholder="新しいパスワードを入力（6文字以上）"
                        />
                      </div>
                      <div className="form-group">
                        <label className="form-label">新しいパスワード（確認）</label>
                        <input
                          type="password"
                          className="form-input"
                          value={passwordForm.confirmNewPassword}
                          onChange={(e) => setPasswordForm({...passwordForm, confirmNewPassword: e.target.value})}
                          placeholder="新しいパスワードを再入力"
                        />
                      </div>
                      <div className="form-actions">
                        <button
                          onClick={() => {
                            setShowPasswordChange(false);
                            setPasswordForm({
                              currentPassword: "",
                              newPassword: "",
                              confirmNewPassword: "",
                            });
                          }}
                          className="btn btn-secondary"
                        >
                          キャンセル
                        </button>
                        <button
                          onClick={handlePasswordChange}
                          className="btn btn-primary"
                          disabled={isChangingPassword}
                        >
                          {isChangingPassword ? "変更中..." : "パスワードを変更"}
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}

            {activeTab === "privacy" && (
              <div className="settings-section">
                <h2>プライバシー設定</h2>
                <div className="form-group">
                  <label className="form-label">プロフィールの公開設定</label>
                  <select className="form-input">
                    <option value="public">公開</option>
                    <option value="private">非公開</option>
                  </select>
                  <p className="form-help">
                    プロフィールを他のユーザーに表示するかどうかを設定できます
                  </p>
                </div>
                <button className="btn btn-primary">設定を保存</button>
              </div>
            )}

            {activeTab === "notifications" && (
              <div className="settings-section">
                <h2>通知設定</h2>
                <div className="form-group">
                  <label className="checkbox-label">
                    <input type="checkbox" defaultChecked />
                    <span>メール通知</span>
                  </label>
                </div>
                <div className="form-group">
                  <label className="checkbox-label">
                    <input type="checkbox" defaultChecked />
                    <span>プッシュ通知</span>
                  </label>
                </div>
                <div className="form-group">
                  <label className="checkbox-label">
                    <input type="checkbox" />
                    <span>マーケティングメール</span>
                  </label>
                </div>
                <button className="btn btn-primary">設定を保存</button>
              </div>
            )}

            {activeTab === "danger" && (
              <div className="settings-section">
                <h2>危険な操作</h2>

                <div className="danger-zone">
                  <h3>ログアウト</h3>
                  <p>現在のセッションからログアウトします。</p>
                  <button onClick={handleLogout} className="btn btn-secondary">
                    ログアウト
                  </button>
                </div>

                <div className="danger-zone">
                  <h3>アカウント削除</h3>
                  <p>
                    アカウントを完全に削除します。この操作は取り消せません。
                  </p>

                  {!showDeleteConfirm ? (
                    <button
                      onClick={() => setShowDeleteConfirm(true)}
                      className="btn btn-danger"
                    >
                      アカウントを削除
                    </button>
                  ) : (
                    <div className="delete-confirmation">
                      <div className="form-group">
                        <label className="form-label">退会理由</label>
                        <select
                          value={deleteForm.reason}
                          onChange={(e) => setDeleteForm({...deleteForm, reason: e.target.value})}
                          className="form-input"
                        >
                          <option value="">選択してください</option>
                          <option value="no-longer-needed">もう必要ない</option>
                          <option value="privacy-concerns">
                            プライバシーの懸念
                          </option>
                          <option value="unsatisfactory-service">
                            サービスに満足していない
                          </option>
                          <option value="other">その他</option>
                        </select>
                      </div>

                      <div className="form-group">
                        <label className="form-label">パスワード確認</label>
                        <input
                          type="password"
                          className="form-input"
                          value={deleteForm.password}
                          onChange={(e) => setDeleteForm({...deleteForm, password: e.target.value})}
                          placeholder="パスワードを入力して削除を確認"
                        />
                      </div>

                      <div className="delete-actions">
                        <button
                          onClick={() => {
                            setShowDeleteConfirm(false);
                            setDeleteForm({ reason: "", password: "" });
                          }}
                          className="btn btn-secondary"
                        >
                          キャンセル
                        </button>
                        <button
                          onClick={handleDeleteAccount}
                          className="btn btn-danger"
                          disabled={isDeletingAccount}
                        >
                          {isDeletingAccount ? "削除中..." : "削除を確定"}
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
