import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile } from "../types";
import "./MyPage.css";

const MyPage: React.FC = () => {
  const { user } = useAuth();
  const { userId } = useParams<{ userId: string }>();
  const [profile, setProfile] = useState<Profile | null>(null);


  useEffect(() => {
    // „É¶„Éº„Ç∂„ÉºID„ÅåÊåáÂÆö„Åï„Çå„Å¶„ÅÑ„ÇãÂ†¥Âêà„ÅØ„Åù„ÅÆ„É¶„Éº„Ç∂„Éº„ÅÆ„Éó„É≠„Éï„Ç£„Éº„É´„ÇíË°®Á§∫
    // ÊåáÂÆö„Åï„Çå„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÅØ„É≠„Ç∞„Ç§„É≥„É¶„Éº„Ç∂„Éº„ÅÆ„Éó„É≠„Éï„Ç£„Éº„É´„ÇíË°®Á§∫
    const targetUserId = userId || user?.id || "1";

    // „É¢„ÉÉ„ÇØ„Éó„É≠„Éï„Ç£„Éº„É´„Éá„Éº„Çø
    const mockProfile: Profile = {
      id: "1",
      userId: targetUserId,
      profileImageUrl:
        "https://via.placeholder.com/300x180/667eea/ffffff?text=Meetolio+ÂêçÂà∫",
      jobTitle: "„Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„Ç®„É≥„Ç∏„Éã„Ç¢",
      company: "Ê†™Âºè‰ºöÁ§æ„ÉÜ„ÉÉ„ÇØ„ÇΩ„É™„É•„Éº„Ç∑„Éß„É≥",
      bio: "React„ÄÅTypeScript„ÄÅNode.js„Çí‰ΩøÁî®„Åó„ÅüWeb„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥ÈñãÁô∫„Å´Âæì‰∫ã„Åó„Å¶„ÅÑ„Åæ„Åô„ÄÇ„É¶„Éº„Ç∂„Éº‰ΩìÈ®ì„ÇíÈáçË¶ñ„Åó„ÅüUI/UX„Éá„Ç∂„Ç§„É≥„ÅåÂæóÊÑè„Åß„Åô„ÄÇÊñ∞„Åó„ÅÑÊäÄË°ì„ÅÆÁøíÂæó„Å®ÂÆüË∑µÁöÑ„Å™„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥ÈñãÁô∫„Å´ÊÉÖÁÜ±„ÇíÊåÅ„Å£„Å¶Âèñ„ÇäÁµÑ„Çì„Åß„ÅÑ„Åæ„Åô„ÄÇ",
      contactInfo: {
        email: "test@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "@techdev",
          linkedin: "linkedin.com/in/techdev",
          facebook: "facebook.com/techdev",
          instagram: "@techdev",
          github: "github.com/techdev",
        },
        website: "https://techdev.example.com",
      },
      skills: ["React", "TypeScript", "Node.js", "CSS", "Git"],
      interests: ["WebÈñãÁô∫", "UI/UX", "ÊäÄË°ìÊõ∏", "„Ç´„É≥„Éï„Ç°„É¨„É≥„Çπ"],
      isPublic: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
  }, [userId, user]);

  if (!profile) {
    return (
      <div className="mypage">
        <div className="container">
          <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>
        </div>
      </div>
    );
  }



  // Ë°®Á§∫„Åô„Çã„É¶„Éº„Ç∂„ÉºÂêç„ÇíÊ±∫ÂÆö
  const displayName = user?.name || "„ÉÜ„ÉÉ„ÇØ Â§™ÈÉé";

  return (
    <div className="mypage">
      <div className="container">
        <div className="profile-section">
          {/* ÂêçÂà∫ÁîªÂÉè„Çª„ÇØ„Ç∑„Éß„É≥ */}
          <div className="business-card-section">
            <div className="card-container">
              <div className="business-card-image">
                <img
                  src="/img/sample.jpeg"
                  alt="ÂêçÂà∫"
                  className="card-image"
                  onError={(e) => {
                    // ÁîªÂÉè„ÅåË™≠„ÅøËæº„ÇÅ„Å™„ÅÑÂ†¥Âêà„ÅÆ„Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØ
                    const target = e.target as HTMLImageElement;
                    target.style.display = 'none';
                    target.nextElementSibling?.classList.remove('hidden');
                  }}
                />
                {/* „Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØÁî®„ÅÆ„Éó„É¨„Éº„Çπ„Éõ„É´„ÉÄ„Éº */}
                <div className="card-placeholder hidden">
                  <div className="placeholder-content">
                    <div className="placeholder-icon">üìá</div>
                    <p className="placeholder-text">ÂêçÂà∫ÁîªÂÉè</p>
                    <p className="placeholder-subtext">ÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶„Åè„Å†„Åï„ÅÑ</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Ëá™Â∑±Á¥π‰ªã„Çª„ÇØ„Ç∑„Éß„É≥ */}
          {profile.bio && (
            <div className="bio-section">
              <div className="bio-header">
                <div className="bio-header-left">
                  <div className="bio-company-name">{profile.company}</div>
                  <div className="bio-job-title">{profile.jobTitle}</div>
                  <div className="bio-person-name">{displayName}</div>
                  <div className="bio-person-furigana">„ÉÜ„ÉÉ„ÇØ „Çø„É≠„Ç¶</div>
                </div>
                <div className="bio-header-right">
                  <div className="bio-icon">üë§</div>
                </div>
              </div>
              <div className="bio-content">
                <p>{profile.bio}</p>
              </div>
            </div>
          )}

          {/* SNS„É™„É≥„ÇØ„Çª„ÇØ„Ç∑„Éß„É≥ */}
          <div className="sns-section">
            <div className="sns-grid">
              {/* „É°„Éº„É´ */}
              <a
                href={`mailto:${profile.contactInfo.email}`}
                className="sns-card email-card"
                target="_blank"
                rel="noopener noreferrer"
              >
                <div className="sns-icon">üìß</div>
                <div className="sns-info">
                  <span className="sns-label">„É°„Éº„É´</span>
                  <span className="sns-value">{profile.contactInfo.email}</span>
                </div>
              </a>

              {/* ÈõªË©±Áï™Âè∑ */}
              {profile.contactInfo.phone && (
                <a
                  href={`tel:${profile.contactInfo.phone}`}
                  className="sns-card phone-card"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <div className="sns-icon">üìû</div>
                  <div className="sns-info">
                    <span className="sns-label">ÈõªË©±Áï™Âè∑</span>
                    <span className="sns-value">
                      {profile.contactInfo.phone}
                    </span>
                  </div>
                </a>
              )}

              {/* „Ç¶„Çß„Éñ„Çµ„Ç§„Éà */}
              {profile.contactInfo.website && (
                <a
                  href={profile.contactInfo.website}
                  className="sns-card website-card"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <div className="sns-icon">üåê</div>
                  <div className="sns-info">
                    <span className="sns-label">„Ç¶„Çß„Éñ„Çµ„Ç§„Éà</span>
                    <span className="sns-value">
                      {profile.contactInfo.website}
                    </span>
                  </div>
                </a>
              )}

              {/* Twitter */}
              {profile.contactInfo.sns?.twitter && (
                <a
                  href={`https://twitter.com/${profile.contactInfo.sns.twitter}`}
                  className="sns-card twitter-card"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <div className="sns-icon">üê¶</div>
                  <div className="sns-info">
                    <span className="sns-label">Twitter</span>
                    <span className="sns-value">
                      {profile.contactInfo.sns.twitter}
                    </span>
                  </div>
                </a>
              )}

              {/* LinkedIn */}
              {profile.contactInfo.sns?.linkedin && (
                <a
                  href={`https://${profile.contactInfo.sns.linkedin}`}
                  className="sns-card linkedin-card"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <div className="sns-icon">üíº</div>
                  <div className="sns-info">
                    <span className="sns-label">LinkedIn</span>
                    <span className="sns-value">
                      {profile.contactInfo.sns.linkedin}
                    </span>
                  </div>
                </a>
              )}

              {/* Facebook */}
              {profile.contactInfo.sns?.facebook && (
                <a
                  href={`https://${profile.contactInfo.sns.facebook}`}
                  className="sns-card facebook-card"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <div className="sns-icon">üìò</div>
                  <div className="sns-info">
                    <span className="sns-label">Facebook</span>
                    <span className="sns-value">
                      {profile.contactInfo.sns.facebook}
                    </span>
                  </div>
                </a>
              )}

              {/* Instagram */}
              {profile.contactInfo.sns?.instagram && (
                <a
                  href={`https://instagram.com/${profile.contactInfo.sns.instagram}`}
                  className="sns-card instagram-card"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <div className="sns-icon">üì∑</div>
                  <div className="sns-info">
                    <span className="sns-label">Instagram</span>
                    <span className="sns-value">
                      {profile.contactInfo.sns.instagram}
                    </span>
                  </div>
                </a>
              )}

              {/* GitHub */}
