import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile } from "../types";


import "./BusinessCardEdit.css";

const BusinessCardEdit: React.FC = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }

    // モックプロフィールデータを取得
    const mockProfile: Profile = {
      id: "1",
      userId: user?.id || "1",
      company: "テック株式会社",
      jobTitle: "フロントエンドエンジニア",
      bio: "Web開発に携わって5年目です。React、TypeScript、Node.jsを中心に開発を行っています。ユーザビリティを重視した設計を心がけています。",
      skills: [
        "React",
        "TypeScript",
        "Node.js",
        "CSS",
        "HTML",
        "JavaScript",
        "Git",
        "AWS",
      ],
      interests: ["プログラミング", "読書", "旅行", "料理", "音楽"],
      contactInfo: {
        email: "taro.tech@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "taro_tech",
          linkedin: "taro-tech",
          github: "taro-tech",
          instagram: "taro_tech_dev",
        },
        website: "https://taro-tech.dev",
      },
      isPublic: true,
      createdAt: new Date("2024-01-01"),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
    setIsLoading(false);
  }, [isAuthenticated, user, navigate]);

  const handleInputChange = (field: keyof Profile, value: any) => {
    if (!profile) return;

    setProfile({
      ...profile,
      [field]: value,
    });
  };

  const handleContactInfoChange = (
    field: keyof Profile["contactInfo"],
    value: any
  ) => {
    if (!profile) return;

    setProfile({
      ...profile,
      contactInfo: {
        ...profile.contactInfo,
        [field]: value,
      },
    });
  };

  const handleSave = async () => {
    if (!profile) return;

    try {
      // モックAPI呼び出し
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // 成功時の処理
      console.log("名刺を保存しました:", profile);
      navigate("/profile/edit");
    } catch (error) {
      console.error("名刺の保存に失敗しました:", error);
    }
  };

  const handleCancel = () => {
    navigate("/profile/edit");
  };

  if (isLoading) {
    return (
      <div className="business-card-edit">
        <div className="container">
          <div className="loading">読み込み中...</div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="business-card-edit">
        <div className="container">
          <div className="error">プロフィールが見つかりません</div>
        </div>
      </div>
    );
  }

  return (
    <div className="business-card-edit">
      <div className="container">
        <div className="edit-form">
          {/* 名刺プレビュー（表面・裏面） */}
          <div className="form-group">
            <label>現在の名刺</label>
            <div className="card-preview-container">
              <div className="preview-section">
                <h4 className="preview-title">表面</h4>
                <div className="card-preview-item">
                  <img
                    src="/img/sample.jpeg"
                    alt="名刺（表面）"
                    className="card-preview-image"
                  />
                </div>
                <div className="preview-actions">
                  <div className="action-item">
                    <button type="button" className="action-btn upload-btn">
                      ⬆
                    </button>
                    <span className="action-label">画像をアップロード</span>
                  </div>
                  <div className="action-item">
                    <button type="button" className="action-btn design-btn">
                      ✏
                    </button>
                    <span className="action-label">自分でデザイン</span>
                  </div>
                </div>
              </div>

              <div className="preview-section">
                <h4 className="preview-title">裏面</h4>
                <div className="card-preview-item">
                  <img
                    src="/img/sample2.jpeg"
                    alt="名刺（裏面）"
                    className="card-preview-image"
                  />
                </div>
                <div className="preview-actions">
                  <div className="action-item">
                    <button type="button" className="action-btn upload-btn">
                      ⬆
                    </button>
                    <span className="action-label">画像をアップロード</span>
                  </div>
                  <div className="action-item">
                    <button type="button" className="action-btn design-btn">
                      ✏
                    </button>
                    <span className="action-label">自分でデザイン</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* 名刺デザイン作成 */}
          <div className="form-group">
            <label>名刺デザイン作成</label>
            <div className="card-designer">
              <div className="designer-preview">
                <div className="card-preview">
                  <div className="preview-header">
                    <input
                      type="text"
                      placeholder="会社名"
                      className="preview-input company-input"
                      value={profile.company || ""}
                      onChange={(e) =>
                        handleInputChange("company", e.target.value)
                      }
                    />
                  </div>
                  <div className="preview-body">
                    <div className="preview-left">
                      <input
                        type="text"
                        placeholder="役職"
                        className="preview-input job-input"
                        value={profile.jobTitle || ""}
                        onChange={(e) =>
                          handleInputChange("jobTitle", e.target.value)
                        }
                      />
                      <input
                        type="text"
                        placeholder="氏名"
                        className="preview-input name-input readonly-field"
                        value={user?.name || ""}
                        disabled
                      />
                    </div>
                    <div className="preview-right">
                      <input
                        type="text"
                        placeholder="メールアドレス"
                        className="preview-input email-input"
                        value={profile.contactInfo.email}
                        onChange={(e) =>
                          handleContactInfoChange("email", e.target.value)
                        }
                      />
                      <input
                        type="text"
                        placeholder="電話番号"
                        className="preview-input phone-input"
                        value={profile.contactInfo.phone || ""}
                        onChange={(e) =>
                          handleContactInfoChange("phone", e.target.value)
                        }
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="designer-controls">
                <p className="designer-hint">
                  上記の入力フィールドを編集すると、名刺のプレビューがリアルタイムで更新されます
                </p>
              </div>
            </div>
          </div>

          {/* 画像アップロード */}
          <div className="form-group">
            <label>画像アップロード</label>
            <div className="image-upload-section">
              <div className="upload-controls">
                <input
                  type="file"
                  id="business-card-upload"
                  accept="image/*"
                  className="file-input"
                  onChange={(e) => {
                    const file = e.target.files?.[0];
                    if (file) {
                      console.log("名刺画像をアップロード:", file.name);
                    }
                  }}
                />
                <label htmlFor="business-card-upload" className="upload-btn">
                  画像を選択
                </label>
                <p className="upload-hint">
                  JPG、PNG、GIF形式の画像をアップロードできます
                </p>
              </div>
            </div>
          </div>

          {/* アクションボタン */}
          <div className="form-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleCancel}
            >
              キャンセル
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleSave}
            >
              保存する
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BusinessCardEdit;
