import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { Profile } from "../types";
import "./ProfileEdit.css";

const ProfileEdit: React.FC = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }

    // „É¢„ÉÉ„ÇØ„Éó„É≠„Éï„Ç£„Éº„É´„Éá„Éº„Çø„ÇíÂèñÂæó
    const mockProfile: Profile = {
      id: "1",
      userId: user?.id || "1",
      company: "„ÉÜ„ÉÉ„ÇØÊ†™Âºè‰ºöÁ§æ",
      jobTitle: "„Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„Ç®„É≥„Ç∏„Éã„Ç¢",
      bio: "WebÈñãÁô∫„Å´Êê∫„Çè„Å£„Å¶5Âπ¥ÁõÆ„Åß„Åô„ÄÇReact„ÄÅTypeScript„ÄÅNode.js„Çí‰∏≠ÂøÉ„Å´ÈñãÁô∫„ÇíË°å„Å£„Å¶„ÅÑ„Åæ„Åô„ÄÇ„É¶„Éº„Ç∂„Éì„É™„ÉÜ„Ç£„ÇíÈáçË¶ñ„Åó„ÅüË®≠Ë®à„ÇíÂøÉ„Åå„Åë„Å¶„ÅÑ„Åæ„Åô„ÄÇ",
      skills: [
        "React",
        "TypeScript",
        "Node.js",
        "CSS",
        "HTML",
        "JavaScript",
        "Git",
        "AWS",
      ],
      interests: ["„Éó„É≠„Ç∞„É©„Éü„É≥„Ç∞", "Ë™≠Êõ∏", "ÊóÖË°å", "ÊñôÁêÜ", "Èü≥Ê•Ω"],
      contactInfo: {
        email: "taro.tech@example.com",
        phone: "090-1234-5678",
        sns: {
          twitter: "taro_tech",
          linkedin: "taro-tech",
          github: "taro-tech",
          instagram: "taro_tech_dev",
        },
        website: "https://taro-tech.dev",
      },
      isPublic: true,
      createdAt: new Date("2024-01-01"),
      updatedAt: new Date(),
    };

    setProfile(mockProfile);
    setIsLoading(false);
  }, [isAuthenticated, user, navigate]);

  const handleInputChange = (field: keyof Profile, value: any) => {
    if (!profile) return;

    setProfile({
      ...profile,
      [field]: value,
    });
  };

  const handleContactInfoChange = (
    field: keyof Profile["contactInfo"],
    value: any
  ) => {
    if (!profile) return;

    setProfile({
      ...profile,
      contactInfo: {
        ...profile.contactInfo,
        [field]: value,
      },
    });
  };

  const handleSnsChange = (
    platform: keyof Profile["contactInfo"]["sns"],
    value: string
  ) => {
    if (!profile) return;

    setProfile({
      ...profile,
      contactInfo: {
        ...profile.contactInfo,
        sns: {
          ...profile.contactInfo.sns,
          [platform]: value,
        },
      },
    });
  };

  const handleSkillsChange = (skills: string[]) => {
    if (!profile) return;

    setProfile({
      ...profile,
      skills,
    });
  };

  const handleInterestsChange = (interests: string[]) => {
    if (!profile) return;

    setProfile({
      ...profile,
      interests,
    });
  };

  const handleSave = async () => {
    if (!profile) return;

    setIsSaving(true);

    try {
      // „É¢„ÉÉ„ÇØAPIÂëº„Å≥Âá∫„Åó
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // ÊàêÂäüÊôÇ„ÅÆÂá¶ÁêÜ
      console.log("„Éó„É≠„Éï„Ç£„Éº„É´„Çí‰øùÂ≠ò„Åó„Åæ„Åó„Åü:", profile);
      navigate("/mypage");
    } catch (error) {
      console.error("„Éó„É≠„Éï„Ç£„Éº„É´„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:", error);
    } finally {
      setIsSaving(false);
    }
  };

  if (isLoading) {
    return (
      <div className="profile-edit">
        <div className="container">
          <div className="loading">Ë™≠„ÅøËæº„Åø‰∏≠...</div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="profile-edit">
        <div className="container">
          <div className="error">„Éó„É≠„Éï„Ç£„Éº„É´„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì</div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-edit">
      <div className="container">


        <div className="edit-form">
          {/* ÂêçÂà∫ */}
          <div className="form-group">
            <label>ÂêçÂà∫</label>
            <div className="business-card-section">
              {/* ÁèæÂú®„ÅÆÂêçÂà∫„Éó„É¨„Éì„É•„Éº */}
              <div className="current-card-preview">
                <img
                  src="/img/sample.jpeg"
                  alt="ÁèæÂú®„ÅÆÂêçÂà∫"
                  className="current-card-image"
                />
              </div>

              {/* ÂêçÂà∫Á∑®ÈõÜ„Ç™„Éó„Ç∑„Éß„É≥ */}
              <div className="card-edit-options">
                <div className="option-buttons">
                  <input
                    type="file"
                    id="business-card-upload"
                    accept="image/*"
                    className="file-input"
                    onChange={(e) => {
                      const file = e.target.files?.[0];
                      if (file) {
                        // „Éï„Ç°„Ç§„É´„Ç¢„ÉÉ„Éó„É≠„Éº„ÉâÂá¶ÁêÜÔºà„É¢„ÉÉ„ÇØÔºâ
                        console.log("ÂêçÂà∫ÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ:", file.name);
                      }
                    }}
                  />
                  <label
                    htmlFor="business-card-upload"
                    className="option-btn upload-btn"
                  >
                    üì∑ ÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ
                  </label>

                  <button
                    type="button"
                    className="option-btn design-btn"
                    onClick={() => {
                      // ÂêçÂà∫„Éá„Ç∂„Ç§„Éä„Éº„ÇíÈñã„ÅèÂá¶ÁêÜÔºà„É¢„ÉÉ„ÇØÔºâ
                      console.log("ÂêçÂà∫„Éá„Ç∂„Ç§„Éä„Éº„ÇíÈñã„Åè");
                    }}
                  >
                    ‚úèÔ∏è ÂêçÂà∫„Çí„Éá„Ç∂„Ç§„É≥
                  </button>
                </div>

                <p className="option-hint">
                  ÁîªÂÉè„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åô„Çã„Åã„ÄÅGUI‰∏ä„ÅßÂêçÂà∫„Çí„Éá„Ç∂„Ç§„É≥„Åß„Åç„Åæ„Åô
                </p>
              </div>
            </div>
          </div>

          {/* ÂêçÂâçÔºàË™≠„ÅøÂèñ„ÇäÂ∞ÇÁî®Ôºâ */}
          <div className="form-group">
            <label htmlFor="name">Ê∞èÂêç</label>
            <input
              type="text"
              id="name"
              value={user?.name || ""}
              disabled
              className="readonly-field"
            />
          </div>

          {/* Ëá™Â∑±Á¥π‰ªã */}
          <div className="form-group">
            <label htmlFor="bio">Ëá™Â∑±Á¥π‰ªã</label>
            <textarea
              id="bio"
              value={profile.bio || ""}
              onChange={(e) => handleInputChange("bio", e.target.value)}
              placeholder="„ÅÇ„Å™„Åü„Å´„Å§„ÅÑ„Å¶Êïô„Åà„Å¶„Åè„Å†„Åï„ÅÑ"
              rows={5}
            />
          </div>

          {/* ‰ºöÁ§æÊÉÖÂ†± */}
          <div className="form-group">
            <label htmlFor="company">‰ºöÁ§æÂêç</label>
            <input
              type="text"
              id="company"
              value={profile.company || ""}
              onChange={(e) => handleInputChange("company", e.target.value)}
              placeholder="‰ºöÁ§æÂêç„ÇíÂÖ•Âäõ"
            />
          </div>

          <div className="form-group">
            <label htmlFor="jobTitle">ÂΩπËÅ∑</label>
            <input
              type="text"
              id="jobTitle"
              value={profile.jobTitle || ""}
              onChange={(e) => handleInputChange("jobTitle", e.target.value)}
              placeholder="ÂΩπËÅ∑„ÇíÂÖ•Âäõ"
            />
          </div>

          {/* ÈÄ£Áµ°ÂÖà */}
          <div className="form-group">
            <label htmlFor="email">„É°„Éº„É´„Ç¢„Éâ„É¨„Çπ</label>
            <input
              type="email"
              id="email"
              value={profile.contactInfo.email}
              onChange={(e) => handleContactInfoChange("email", e.target.value)}
              placeholder="example@email.com"
            />
          </div>

          <div className="form-group">
            <label htmlFor="website">„Ç¶„Çß„Éñ„Çµ„Ç§„Éà</label>
            <input
              type="url"
              id="website"
              value={profile.contactInfo.website || ""}
              onChange={(e) =>
                handleContactInfoChange("website", e.target.value)
              }
              placeholder="https://example.com"
            />
          </div>

          {/* SNS */}
          <div className="form-group">
            <label htmlFor="twitter">Twitter</label>
            <input
              type="text"
              id="twitter"
              value={profile.contactInfo.sns.twitter || ""}
              onChange={(e) => handleSnsChange("twitter", e.target.value)}
              placeholder="@username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="github">GitHub</label>
            <input
              type="text"
              id="github"
              value={profile.contactInfo.sns.github || ""}
              onChange={(e) => handleSnsChange("github", e.target.value)}
              placeholder="username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="linkedin">LinkedIn</label>
            <input
              type="text"
              id="linkedin"
              value={profile.contactInfo.sns.linkedin || ""}
              onChange={(e) => handleSnsChange("linkedin", e.target.value)}
              placeholder="username"
            />
          </div>

          {/* „Çπ„Ç≠„É´ */}
          <div className="form-group">
            <label>„Çπ„Ç≠„É´„ÉªÂ∞ÇÈñÄÂàÜÈáé</label>
            <div className="tags-input">
              <div className="tags-display">
                {profile.skills.map((skill, index) => (
                  <span key={index} className="tag skill-tag">
                    {skill}
                    <button
                      type="button"
                      className="tag-remove"
                      onClick={() => {
                        const newSkills = profile.skills.filter(
                          (_, i) => i !== index
                        );
                        handleSkillsChange(newSkills);
                      }}
                    >
                      √ó
                    </button>
                  </span>
                ))}
              </div>
              <input
                type="text"
                placeholder="„Çπ„Ç≠„É´„ÇíÂÖ•Âäõ„Åó„Å¶Enter"
                className="tag-input"
                onKeyPress={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    const input = e.currentTarget;
                    const value = input.value.trim();
                    if (value && !profile.skills.includes(value)) {
                      handleSkillsChange([...profile.skills, value]);
                      input.value = "";
                    }
                  }
                }}
              />
            </div>
          </div>

          {/* Ë∂£Âë≥„ÉªËààÂë≥ */}
          <div className="form-group">
            <label>Ë∂£Âë≥„ÉªËààÂë≥</label>
            <div className="tags-input">
              <div className="tags-display">
                {profile.interests.map((interest, index) => (
                  <span key={index} className="tag interest-tag">
                    {interest}
                    <button
                      type="button"
                      className="tag-remove"
                      onClick={() => {
                        const newInterests = profile.interests.filter(
                          (_, i) => i !== index
                        );
                        handleInterestsChange(newInterests);
                      }}
                    >
                      √ó
                    </button>
                  </span>
                ))}
              </div>
              <input
                type="text"
                placeholder="Ë∂£Âë≥„ÉªËààÂë≥„ÇíÂÖ•Âäõ„Åó„Å¶Enter"
                className="tag-input"
                onKeyPress={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    const input = e.currentTarget;
                    const value = input.value.trim();
                    if (value && !profile.interests.includes(value)) {
                      handleInterestsChange([...profile.interests, value]);
                      input.value = "";
                    }
                  }
                }}
              />
            </div>
          </div>

          {/* ‰øùÂ≠ò„Éú„Çø„É≥ */}
          <div className="form-actions">
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleSave}
              disabled={isSaving}
            >
              {isSaving ? "‰øùÂ≠ò‰∏≠..." : "‰øùÂ≠ò„Åô„Çã"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileEdit;
