import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import "./Settings.css";

const Settings: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("profile");
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [deleteReason, setDeleteReason] = useState("");

  const handleLogout = () => {
    if (window.confirm("ログアウトしますか？")) {
      logout();
      navigate("/login");
    }
  };

  const handleDeleteAccount = () => {
    if (!deleteReason.trim()) {
      alert("退会理由を選択してください");
      return;
    }

    if (
      window.confirm(
        "本当にアカウントを削除しますか？この操作は取り消せません。"
      )
    ) {
      // 実際の実装では、APIを呼び出してアカウントを削除
      alert("アカウントが削除されました");
      logout();
      navigate("/");
    }
  };

  if (!user) {
    return null;
  }

  return (
    <div className="settings">
      <div className="container">
        <div className="settings-header">
          <h1>設定</h1>
        </div>

        <div className="settings-content">
          <div className="settings-sidebar">
            <nav className="settings-nav">
              <button
                className={`nav-item ${
                  activeTab === "profile" ? "active" : ""
                }`}
                onClick={() => setActiveTab("profile")}
              >
                プロフィール設定
              </button>
              <button
                className={`nav-item ${
                  activeTab === "account" ? "active" : ""
                }`}
                onClick={() => setActiveTab("account")}
              >
                アカウント設定
              </button>
              <button
                className={`nav-item ${
                  activeTab === "privacy" ? "active" : ""
                }`}
                onClick={() => setActiveTab("privacy")}
              >
                プライバシー設定
              </button>
              <button
                className={`nav-item ${
                  activeTab === "notifications" ? "active" : ""
                }`}
                onClick={() => setActiveTab("notifications")}
              >
                通知設定
              </button>
              <button
                className={`nav-item ${activeTab === "danger" ? "active" : ""}`}
                onClick={() => setActiveTab("danger")}
              >
                危険な操作
              </button>
            </nav>
          </div>

          <div className="settings-main">
            {activeTab === "profile" && (
              <div className="settings-section">
                <h2>プロフィール設定</h2>
                <p>プロフィール情報の編集は、マイページから行えます。</p>
                <button
                  onClick={() => navigate("/mypage")}
                  className="btn btn-primary"
                >
                  マイページへ移動
                </button>
              </div>
            )}

            {activeTab === "account" && (
              <div className="settings-section">
                <h2>アカウント設定</h2>
                <div className="form-group">
                  <label className="form-label">メールアドレス</label>
                  <input
                    type="email"
                    className="form-input"
                    value={user.email}
                    disabled
                  />
                  <p className="form-help">
                    メールアドレスの変更は現在サポートされていません
                  </p>
                </div>
                <button className="btn btn-primary" disabled>
                  パスワード変更
                </button>
              </div>
            )}

            {activeTab === "privacy" && (
              <div className="settings-section">
                <h2>プライバシー設定</h2>
                <div className="form-group">
                  <label className="form-label">プロフィールの公開設定</label>
                  <select className="form-input">
                    <option value="public">公開</option>
                    <option value="private">非公開</option>
                  </select>
                  <p className="form-help">
                    プロフィールを他のユーザーに表示するかどうかを設定できます
                  </p>
                </div>
                <button className="btn btn-primary">設定を保存</button>
              </div>
            )}

            {activeTab === "notifications" && (
              <div className="settings-section">
                <h2>通知設定</h2>
                <div className="form-group">
                  <label className="checkbox-label">
                    <input type="checkbox" defaultChecked />
                    <span>メール通知</span>
                  </label>
                </div>
                <div className="form-group">
                  <label className="checkbox-label">
                    <input type="checkbox" defaultChecked />
                    <span>プッシュ通知</span>
                  </label>
                </div>
                <div className="form-group">
                  <label className="checkbox-label">
                    <input type="checkbox" />
                    <span>マーケティングメール</span>
                  </label>
                </div>
                <button className="btn btn-primary">設定を保存</button>
              </div>
            )}

            {activeTab === "danger" && (
              <div className="settings-section">
                <h2>危険な操作</h2>

                <div className="danger-zone">
                  <h3>ログアウト</h3>
                  <p>現在のセッションからログアウトします。</p>
                  <button onClick={handleLogout} className="btn btn-secondary">
                    ログアウト
                  </button>
                </div>

                <div className="danger-zone">
                  <h3>アカウント削除</h3>
                  <p>
                    アカウントを完全に削除します。この操作は取り消せません。
                  </p>

                  {!showDeleteConfirm ? (
                    <button
                      onClick={() => setShowDeleteConfirm(true)}
                      className="btn btn-danger"
                    >
                      アカウントを削除
                    </button>
                  ) : (
                    <div className="delete-confirmation">
                      <div className="form-group">
                        <label className="form-label">退会理由</label>
                        <select
                          value={deleteReason}
                          onChange={(e) => setDeleteReason(e.target.value)}
                          className="form-input"
                        >
                          <option value="">選択してください</option>
                          <option value="no-longer-needed">もう必要ない</option>
                          <option value="privacy-concerns">
                            プライバシーの懸念
                          </option>
                          <option value="unsatisfactory-service">
                            サービスに満足していない
                          </option>
                          <option value="other">その他</option>
                        </select>
                      </div>

                      <div className="delete-actions">
                        <button
                          onClick={() => setShowDeleteConfirm(false)}
                          className="btn btn-secondary"
                        >
                          キャンセル
                        </button>
                        <button
                          onClick={handleDeleteAccount}
                          className="btn btn-danger"
                        >
                          削除を確定
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
