import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import {
  ChangeEmailForm,
  ChangePasswordForm,
  DeleteAccountForm,
} from "../types";
import "./Settings.css";

const Settings: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  // メールアドレス変更
  const [showEmailChange, setShowEmailChange] = useState(false);
  const [emailForm, setEmailForm] = useState<ChangeEmailForm>({
    currentEmail: user?.email || "",
    newEmail: "",
    password: "",
  });
  const [isChangingEmail, setIsChangingEmail] = useState(false);

  // パスワード変更
  const [showPasswordChange, setShowPasswordChange] = useState(false);
  const [passwordForm, setPasswordForm] = useState<ChangePasswordForm>({
    currentPassword: "",
    newPassword: "",
    confirmNewPassword: "",
  });
  const [isChangingPassword, setIsChangingPassword] = useState(false);

  // 退会処理
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [deleteForm, setDeleteForm] = useState<DeleteAccountForm>({
    reason: "",
    password: "",
  });
  const [isDeletingAccount, setIsDeletingAccount] = useState(false);

  // メールアドレス変更
  const handleEmailChange = async () => {
    if (!emailForm.newEmail || !emailForm.password) {
      alert("新しいメールアドレスとパスワードを入力してください");
      return;
    }

    if (emailForm.newEmail === emailForm.currentEmail) {
      alert("新しいメールアドレスが現在のものと同じです");
      return;
    }

    setIsChangingEmail(true);
    try {
      // モックAPI呼び出し
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // 成功時の処理
      alert("メールアドレスが変更されました");
      setShowEmailChange(false);
      setEmailForm({
        currentEmail: emailForm.newEmail,
        newEmail: "",
        password: "",
      });
    } catch (error) {
      alert("メールアドレスの変更に失敗しました");
    } finally {
      setIsChangingEmail(false);
    }
  };

  // パスワード変更
  const handlePasswordChange = async () => {
    if (
      !passwordForm.currentPassword ||
      !passwordForm.newPassword ||
      !passwordForm.confirmNewPassword
    ) {
      alert("すべてのフィールドを入力してください");
      return;
    }

    if (passwordForm.newPassword !== passwordForm.confirmNewPassword) {
      alert("新しいパスワードが一致しません");
      return;
    }

    if (passwordForm.newPassword.length < 6) {
      alert("新しいパスワードは6文字以上で入力してください");
      return;
    }

    setIsChangingPassword(true);
    try {
      // モックAPI呼び出し
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // 成功時の処理
      alert("パスワードが変更されました");
      setShowPasswordChange(false);
      setPasswordForm({
        currentPassword: "",
        newPassword: "",
        confirmNewPassword: "",
      });
    } catch (error) {
      alert("パスワードの変更に失敗しました");
    } finally {
      setIsChangingPassword(false);
    }
  };

  // 退会処理
  const handleDeleteAccount = async () => {
    if (!deleteForm.reason || !deleteForm.password) {
      alert("退会理由とパスワードを入力してください");
      return;
    }

    if (
      window.confirm(
        "本当にアカウントを削除しますか？この操作は取り消せません。"
      )
    ) {
      setIsDeletingAccount(true);
      try {
        // モックAPI呼び出し
        await new Promise((resolve) => setTimeout(resolve, 1000));

        // 成功時の処理
        alert("アカウントが削除されました");
        logout();
        navigate("/login");
      } catch (error) {
        alert("アカウントの削除に失敗しました");
      } finally {
        setIsDeletingAccount(false);
      }
    }
  };

  if (!user) {
    return null;
  }

  return (
    <div className="settings">
      <div className="container">
        <div className="settings-content">
          <div className="settings-main">
            <div className="settings-section">
              <h2 className="settings-title">アカウント設定</h2>

                             {/* メールアドレス変更 */}
               <div 
                 className="account-item clickable"
                 onClick={() => !showEmailChange && setShowEmailChange(true)}
               >
                 <div className="account-item-content">
                   <div className="account-item-label">メールアドレス</div>
                   <div className="account-item-value">{user.email}</div>
                 </div>
                 {!showEmailChange && (
                   <div className="account-change-hint">変更 &gt;</div>
                 )}
               </div>

              {showEmailChange && (
                <div className="change-email-form">
                  <div className="form-group">
                    <label className="form-label">新しいメールアドレス</label>
                    <input
                      type="email"
                      className="form-input"
                      value={emailForm.newEmail}
                      onChange={(e) =>
                        setEmailForm({
                          ...emailForm,
                          newEmail: e.target.value,
                        })
                      }
                      placeholder="新しいメールアドレスを入力"
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">現在のパスワード</label>
                    <input
                      type="password"
                      className="form-input"
                      value={emailForm.password}
                      onChange={(e) =>
                        setEmailForm({
                          ...emailForm,
                          password: e.target.value,
                        })
                      }
                      placeholder="現在のパスワードを入力"
                    />
                  </div>
                  <div className="form-actions">
                    <button
                      onClick={() => {
                        setShowEmailChange(false);
                        setEmailForm({
                          currentEmail: user?.email || "",
                          newEmail: "",
                          password: "",
                        });
                      }}
                      className="btn btn-secondary"
                    >
                      キャンセル
                    </button>
                    <button
                      onClick={handleEmailChange}
                      className="btn btn-primary"
                      disabled={isChangingEmail}
                    >
                      {isChangingEmail ? "変更中..." : "メールアドレスを変更"}
                    </button>
                  </div>
                </div>
              )}

                             {/* パスワード変更 */}
               <div 
                 className="account-item clickable"
                 onClick={() => !showPasswordChange && setShowPasswordChange(true)}
               >
                 <div className="account-item-content">
                   <div className="account-item-label">パスワード</div>
                   <div className="account-item-value">********</div>
                 </div>
                 {!showPasswordChange && (
                   <div className="account-change-hint">変更 &gt;</div>
                 )}
               </div>

              {showPasswordChange && (
                <div className="change-password-form">
                  <div className="form-group">
                    <label className="form-label">現在のパスワード</label>
                    <input
                      type="password"
                      className="form-input"
                      value={passwordForm.currentPassword}
                      onChange={(e) =>
                        setPasswordForm({
                          ...passwordForm,
                          currentPassword: e.target.value,
                        })
                      }
                      placeholder="現在のパスワードを入力"
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">新しいパスワード</label>
                    <input
                      type="password"
                      className="form-input"
                      value={passwordForm.newPassword}
                      onChange={(e) =>
                        setPasswordForm({
                          ...passwordForm,
                          newPassword: e.target.value,
                        })
                      }
                      placeholder="新しいパスワードを入力（6文字以上）"
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">
                      新しいパスワード（確認）
                    </label>
                    <input
                      type="password"
                      className="form-input"
                      value={passwordForm.confirmNewPassword}
                      onChange={(e) =>
                        setPasswordForm({
                          ...passwordForm,
                          confirmNewPassword: e.target.value,
                        })
                      }
                      placeholder="新しいパスワードを再入力"
                    />
                  </div>
                  <div className="form-actions">
                    <button
                      onClick={() => {
                        setShowPasswordChange(false);
                        setPasswordForm({
                          currentPassword: "",
                          newPassword: "",
                          confirmNewPassword: "",
                        });
                      }}
                      className="btn btn-secondary"
                    >
                      キャンセル
                    </button>
                    <button
                      onClick={handlePasswordChange}
                      className="btn btn-primary"
                      disabled={isChangingPassword}
                    >
                      {isChangingPassword ? "変更中..." : "パスワードを変更"}
                    </button>
                  </div>
                </div>
              )}

                             {/* 退会処理 */}
               <div 
                 className="account-item clickable"
                 onClick={() => !showDeleteConfirm && setShowDeleteConfirm(true)}
               >
                 <div className="account-item-content">
                   <div className="account-item-label">退会処理</div>
                   <div className="account-item-value">アカウントを削除</div>
                 </div>
               </div>

              {showDeleteConfirm && (
                <div className="delete-confirmation">
                  <div className="form-group">
                    <label className="form-label">退会理由</label>
                    <select
                      value={deleteForm.reason}
                      onChange={(e) =>
                        setDeleteForm({
                          ...deleteForm,
                          reason: e.target.value,
                        })
                      }
                      className="form-input"
                    >
                      <option value="">選択してください</option>
                      <option value="no-longer-needed">もう必要ない</option>
                      <option value="privacy-concerns">
                        プライバシーの懸念
                      </option>
                      <option value="unsatisfactory-service">
                        サービスに満足していない
                      </option>
                      <option value="other">その他</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">パスワード確認</label>
                    <input
                      type="password"
                      className="form-input"
                      value={deleteForm.password}
                      onChange={(e) =>
                        setDeleteForm({
                          ...deleteForm,
                          password: e.target.value,
                        })
                      }
                      placeholder="パスワードを入力して削除を確認"
                    />
                  </div>

                  <div className="delete-actions">
                    <button
                      onClick={() => {
                        setShowDeleteConfirm(false);
                        setDeleteForm({ reason: "", password: "" });
                      }}
                      className="btn btn-secondary"
                    >
                      キャンセル
                    </button>
                    <button
                      onClick={handleDeleteAccount}
                      className="btn btn-danger"
                      disabled={isDeletingAccount}
                    >
                      {isDeletingAccount ? "削除中..." : "削除を確定"}
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
